package com.javaex.practice;

public class Ex04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// ";"가 빠짐
		//int x, y 
		int x, y;
		
		
		// 둘 다 ";"가 빠짐
		//x = 10
		//y = 20
		x = 10;
		y = 20;
		
		
		// ";"가 빠짐, sum정의가 안됨
		int sum = 0;
		
		//sum = x + y
		sum = x + y;
		
		// ' -> " 수정
		//System.out.println("합은' + sum);
		System.out.println("합은" + sum);
		
		
	}

}
